mapper.select:
select id, begin_time, end_time, promotion_sn, pay_strategy, add_time, update_time, be_deleted, descr
from promotion
where goods_id = #{id}

mapper.update:
update product
product_sn=#{productSn}, product_property=#{productProperty}, stock=#{stock}, retail_price=#{retailPrice}, purchase_price=#{purchasePrice}, add_time=#{addTime}, update_time=#{updateTime}, be_delete=#{beDelete}, goods_id=#{goodsId},
WHERE id=#{id}
